import { useState, useEffect, useCallback } from 'react'
import { API_URL, API_KEY } from '../../config'

export const useMovieFetch = movieId => {
    const [state, setState] = useState({})
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(false)

    const fetchData = useCallback(async () => {
        setError(false)
        setLoading(true)
        console.log('fetchData')
        try {
            const endpoint = `${API_URL}/movie/${movieId}?api_key=${API_KEY}`
            const result = await (await (await fetch(endpoint)).json())
            console.log(result)
            const creditsEndpoint = `${API_URL}movie/${movieId}/credits?api_key=${API_KEY}`
            const creditsResult = await (await fetch(creditsEndpoint)).json()
            console.log(creditsResult)
            const directors = creditsResults.crew.filter( member => member.job ==='Director')
            const writers = creditsResult.crew.filter( member => member.job === 'Screenplay')

        } catch (error) {
            setError(true)
        }
        setLoading(false)
    }, [movieId])
    useEffect(() => {
        fetchData()
    }, [fetchData])
    return [state, loading, error]
}
