import React, { useState } from 'react'

import {
    API_URL,
    API_KEY,
    IMAGE_BASE_URL,
    POSTER_SIZE,
    BACKDROP_SIZE
} from '../config'
import HeroImage from './elements/HeroImage'
import SearchBar from './elements/SearchBar'
import Grid from './elements/Grid'
import MovieThumb from './elements/MovieThumb'
import LoadMoreBtn from './elements/LoadMoreBtn'
import Spinner from './elements/Spinner'
import RMDBLogo from './images/reactMovie_logo.png'

// Custom Hook
import { useHomeFetch } from './hooks/useHomeFetch'
import NoImage from './images/no_image.jpg'

const Home = () => {

    const [{
        state: {movies, currentPage, heroImage, totalPages }, 
            loading, 
            error
        },
        fetchMovies
    ] = useHomeFetch()
    const [searchTerm, setSearchTerm] = useState('')

    const loadMoreMovies = () => {
        const searchEndPoint =`${API_URL}search/movie?api_key=${API_KEY}&query=${searchTerm}&page=${currentPage+1}}`
        const popularEndPoint = `${API_URL}movie/popular?api_key=${API_KEY}&page=${currentPage + 1}`
    }
    if (error) return <div>Something went wrong</div>
    if (!movies[0]) return <Spinner />
    return (
        <>
            <HeroImage 
                image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}${heroImage.backdrop_path}`}
                title={heroImage.original_title}
                text={heroImage.overview}
            /> 
            <SearchBar />
            <Grid header={searchTerm ? 'Search Result' : 'Popular Movies'} >
                {movies.map (movie => (
                    <MovieThumb
                        key={movie.id}
                        clickable
                        image={movie.poster_path 
                            ? `${IMAGE_BASE_URL}${POSTER_SIZE}${movie.poster_path}`
                            : NoImage
                         }
                         movieId={movie.id}
                         movieName={movie.original_title}
                    />
                ))}
            </Grid>
            <LoadMoreBtn />
            <Spinner />
        </>
    )
}
export default Home
